import React, {Component} from 'react'
import '../App.css'
import uparrow from '../up arrow.jpg'
import downarrow from '../down arrow.jpg'
import {Extract} from './listextractor'
import {Personlistmaker} from './personlist.js'
import {VitalsTable} from './vitalstable'
import {Link,Route} from 'react-router-dom'
import List from '../docpat.json'

export class Alerts extends Component{
    constructor(props){
        super(props)
        this.state={
            name_list:[],
            id_list:[]
        }
        this.show_list=this.show_list.bind(this);
        //this.close_list=this.show_list.bind(this); binding here uses prev state, therefore bound in onClick
    }
    show_list(){
        this.setState({
            name_list:['Sumanth','Saketh','Kunar','Sunni']
        })
        //console.log(this.state.name_list)
    }
    close_list(){
        this.setState({
            name_list:[]
        })
        console.log("on change works!")
    }
    
    render(){
        return(
        <div>
            <div className="alert_box_title">
                Alerts
            </div>
            <div className="patients">
                Sumanth
            </div>
            <div className="patients">
                Rakesh
            </div>
            <div className="patients">
                Murkhi
            </div>
            <div className="patients">
                Kishore
            </div>
            {/* <div className="patients">
               <Personlistmaker name_list={this.state.name_list} />
           </div> */}
        </div>
        )
    }
}

export class Patients extends Component{
    constructor(props){
        super(props)
        this.state={
            name_list:[],
            id_list:[]
        }
        this.show_list=this.show_list.bind(this);
        //this.update_state=this.update_state.bind(this);
        //this.close_list=this.show_list.bind(this); binding here uses prev state, therefore bound in onClick
    }
    
    // extract(name){
    //     console.log("inisde list ectractor method")
    //     let name_list=[],id_list=[],i=0
    //     List.forEach((p)=>{
    //         if(p.name===name){
    //             name_list=p.patients.map((e)=>e[0])
    //             id_list=p.patients.map((e)=>e[1])
    //         }
    //     })
    //     console.log(id_list)
    //     return name_list
    // }


    show_list(temp_list,tempid_list){
        this.setState({
            name_list:temp_list,
            id_list:tempid_list
        })
        // this.setState({
        //     name_list:['a','c']
        // })
    }



    update_state(){
                // let temp_list=['Sumanth','Rakesh','Saketh','Rakim','Kunar']
              //  alert("reached the fn!")    
        // if(this.state.name_list){
            // let temp_list=[]
            // let tempid_list=[]
            let [temp_list,tempid_list]=Extract("Kumar")
            //alert("reached the fn!")
            //console.log(temp_list);
            //this.show_list(temp_list)
            this.setState({
                name_list:temp_list,
                id_list:tempid_list
            })
            //alert(this.state.name_list)
        // }

    }

    close_list(){
        this.setState({
            name_list:[],
            id_list:[]
        })
        console.log("on change works!")
    }



    render(){
        return(
        <div>
        
            <div className="patient_box_title">
               Registered Patients
               <img src={downarrow} className="down" onClick={this.update_state.bind(this)}  height="20px" width="20px" role="button" tabIndex="0" alt="exapand"></img>
               <img src={uparrow}  className ="up"onClick={this.close_list.bind(this)} height="20px" width="20px" role="button" tabIndex="0" alt="exapand"></img>
           </div>
            <div className="patients">
                <Personlistmaker  name_list={this.state.name_list} id_list={this.state.id_list} /> 
           </div> 
        </div>
        )
    }
}
