import React, { Component } from 'react'
import { BrowserRouter,Switch, Route, Redirect, Link} from 'react-router-dom';
import {SignIn} from './signin'
import { SignUp } from './signup';
import {Test} from './test'

export class Home extends Component {

    constructor(props){
        super(props)
        this.State ={
        signinClicked:0,
        signupClicked:0
        }
        //this.trigger_signin=this.trigger_signin.bind(this);
        //this.trigger_signup=this.trigger_signup.bind(this);
    }

    trigger_signin(e){
        e.persist();
        this.setState((state) => {
            return {
                signinClicked : 1
            }
        })
    }

    trigger_signup(e){
        e.persist();
        this.setState((state) => {
            return {
                signupClicked : 1
            }
        })
    }
    render(){
        return (
            <div className="main" >
                {/* <BrowserRouter>
                    <Link to="/">Home</Link>
                    <Link to="/signin">Signin</Link>
                    <Route to="/home" component={Home}></Route>
                    <Route to="/signin" component={SignIn}></Route>
                </BrowserRouter> */}
                <head>
                    <title>Covid-19 Patient Monitor</title>
                </head>
                <div >
                    <header className="heading">
                        Covid-19 Patient Monitor System
                    </header>
                    <br></br>
                    <div className="description">
                         Welcome !
                        This system allows doctors to monitor vital information about a patient.
                        Patients can provide their details to the server , and admins can managae these profiles
                        {/* <img src={BG}></img> */}
                    </div>
                    <div className="signin">
                        Have an account? Log in 
                        {/* <button className="in" onClick={signin} >SIGN IN</button> */}
                        <BrowserRouter>
                            <Link to="/signin" className="in" onClick={this.trigger_signin}>Sign In</Link>
                            <Route path="/signin" exact strict render={()=>(
                                (this.state.signinClicked===1)?(<SignIn />):(<Redirect to="/"/>)
                            )}/>
                        </BrowserRouter>
                    </div>
                    <div className="signup">
                        Dont have an account? Sign up!
                        {/* <button className="in">SIGN UP</button> */}
                        <BrowserRouter>
                            <Link to="/signup" className="in" onClick={this.trigger_signup} >Sign Up</Link>
                            <Route path="/signup" component={SignUp} />
                        </BrowserRouter>
                    </div>
                    
                </div>
    
            </div>
        )
   
}
}
