import React, { Component, useState } from "react";
import axios from "axios";
import { DocTable } from "./docstable";
import { PatTable } from "./pattable";
import { StylesProvider } from "@material-ui/core/styles";
//import for register form
import "./form.css";

export class Patients extends Component {
  render() {
    return (
      <div>
        <div className="docpat">
          <StylesProvider injectFirst>
            <PatTable />
          </StylesProvider>
        </div>
      </div>
    );
  }
}

export class Doctors extends Component {
  render() {
    return (
      <div>
        <div className="docpat">
          <StylesProvider injectFirst>
            <DocTable />
          </StylesProvider>
        </div>
      </div>
    );
  }
}

export function RegisterPatient() {
  //define state to hold input field values
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [sex, setSex] = useState("");
  const [guardianName, setguardianName] = useState("");
  const [age, setAge] = useState("");
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [adminId, setaId] = useState("");
  const [doctorId, setdId] = useState("");

  // function to spost data
  const sendData = (x) => {
    console.log("handle submit");
    axios
      .post(
        "http://dceed99d2fec.ngrok.io/covid19_patient_monitor/add_patient",
        {
          x,
        }
      )
      .then((res) => console.log("response api= ", res.data))
      .catch((err) => {
        console.log("error ", err);
      });
  };
  const handleSubmit = (e) => {
    // console.log("handle submit");
    e.preventDefault();
    let post = {
      name: name,
      address: address,
      password: password,
      email: email,
      phone: phone,
      sex: sex,
      guardianName: guardianName,
      age: age,
      height: height,
      weight: weight,
      adminId: adminId,
      doctorId: doctorId,
    };
    post = JSON.stringify(post);

    //send post data to API
    sendData(post);
  };

  return (
    <div>
      <div className="wrapper">
        <div className="title">Patient Registration</div>
        <div className="form">
          <div className="inputfield">
            <label>Name</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="inputfield">
            <label>Email Address</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="inputfield">
            <label>Password</label>
            <input
              type="password"
              className="input"
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="inputfield">
            <label>Phone Number</label>
