import React from 'react';
import MaterialTable from 'material-table';

export  function PatTable() {
  const [state, setState] = React.useState({
    columns: [
      { title: 'Name', field: 'name' },
      { title: 'Unique ID', field: 'id' },
    ],
    data: [
      { name: 'Mehmet', id: 307 },
      { name: 'Zerya ',id:891},
      { name: 'Kumar',id:644},
      { name: 'Kovind19',id:6969}
    ],
  });

  return (
    <MaterialTable
      title="Registered Doctors"
      columns={state.columns}
      data={state.data}
      options={{
          paging:false
      }}
      editable={{
/*         onRowAdd: (newData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.push(newData);
                return { ...prevState, data };
              });
            }, 600);
          }), */
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                setState((prevState) => {
                  const data = [...prevState.data];
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
      }}
    />
  );
}
