import React, { Component } from 'react'
import { BrowserRouter,Switch, Route, Redirect, Link} from 'react-router-dom';
import {SignIn} from './signin'
import { SignUp } from './signup';
import {Test} from './test'

export class Home extends Component {

    constructor(props){
        super(props)
        this.State ={
        signinClicked:0,
        signupClicked:0
        }
        this.trigger_signin=this.trigger_signin.bind(this);
        this.trigger_signup=this.trigger_signup.bind(this);
    }

    trigger_signin(){
        console.log("Sign in clicked!")
        this.setState({
            signinClicked: 1
        })
        
    }

    trigger_signup(){
        console.log("Sign up clicked!")
        this.setState({
            signupClicked: 1
        })
        
    }

    create_component(){
        let show
        if(this.state.signinClicked===1)
            show=<SignIn/>;
        else if(this.state.signupClicked===1)
            show=<SignUp/>;
        else
            show=<Home/>;
        return show;
    }
    render(){
        // let show=this.create_component()
        return (
            
            <div className="main" >
                {console.log(this.state)}
                {/* <BrowserRouter>
                    <Link to="/">Home</Link>
                    <Link to="/signin">Signin</Link>
                    <Route to="/home" component={Home}></Route>
                    <Route to="/signin" component={SignIn}></Route>
                </BrowserRouter> */}
                <head>
                    <title>Covid-19 Patient Monitor</title>
                </head>
                <div >
                    <header className="heading">
                        Covid-19 Patient Monitor System
                    </header>
                    <br></br>
                    <div className="description">
                         Welcome !
                        This system allows doctors to monitor vital information about a patient.
                        Patients can provide their details to the server , and admins can managae these profiles
                        {/* <img src={BG}></img> */}
                    </div>
                    
                    
                </div>
    
            </div>
        )
   
}
}
