import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import { PatTableExtract } from "./listextractor";
import axios from "axios";
import { Extract } from "./extract.js";

export function PatTable() {
  //set state for apidata
  const [apiData, setapiData] = useState([]);
  const [datalen, setdataLen] = useState(0);

  //request data from api
  async function getApiData() {
    await axios
      .get("http://7f528d0c0908.ngrok.io/covid19_patient_monitor/get_info", {
        params: {
          a_id: "ADMIN123",
        },
      })
      .then((res) => {
        // console.log("api response ", res.data.patients);
        setapiData(res.data.patients);
        //console.log("state= ", apiData);
      })
      .catch((err) => console.log("error ", err));
  }

  let create_object = () => {
    return {
      name_list: 0,
      address: 0,
      patientId: 0,
      phone: 0,
      guardianName: 0,
      doctorId: 0,
      sex: 0,
      weight: 0,
      height: 0,
      age: 0,
      email: 0,
    };
  };

  function table_data() {
    //await getApiData();
    // let len = apiData;
    getApiData();
    let Details = apiData;
    console.log("api obj details ", apiData);
    // console.log("detaiks name ", apiData[1].name);
    // for (var key in Details) console.log("details[k]= ", Details[key].name);
    let d = [];
    for (let j = 0; j < Object.keys(Details).length; j++)
      d[j] = create_object();
    console.log("table data: ", d);
    // console.log("d name ", d[0].name);

    // for (let j = 0; j < 7; j++) {
    //   d[j].name = Details[j].name;
    //   d[j].address = Details[j].address;
    //   d[j].patientId = Details[j].patientId;
    //   d[j].phone = Details[j].phone;
    //   d[j].guardianName = Details[j].guardianName;
    //   d[j].doctorId = Details[j].doctorId;
    //   d[j].sex = Details[j].sex;
    //   d[j].weight = Details[j].weight;
    //   d[j].height = Details[j].height;
    //   d[j].age = Details[j].age;
    // }

    //console.log("table data: ", d);
    return d;
  }
  let d = table_data();
  // console.log("table data: ", d);

  let [name_list, id_list] = PatTableExtract();

  //try to make a func which would return {name:null,id:null} and make this return happen as many times as there are names in the name list
  let data_fn = (name_list, id_list) => {
    let data = [];
    for (let j = 0; j < name_list.length; j++) data[j] = create_object();
    // data[0].name=n.forEach((p)=>p)
    for (let j = 0; j < name_list.length; j++) {
      data[j].name = name_list[j];
      data[j].id = id_list[j];
    }
    return data;
  };

  //console.log("apidata= ", apiData);

  let [state, setState] = React.useState({
    columns: [
      { title: "Name", field: "name" },
      { title: "Patient ID", field: "id" },
      { title: "Email", field: "email" },
      { title: "Password", field: "password" },
      { title: "Address", field: "address" },
      { title: "Phone Number", field: "phone" },
      { title: "Gender", field: "sex" },
      { title: "Name of Guardian", field: "guardianName" },
      { title: "Age", field: "age" },
      { title: "Height", field: "height" },
      { title: "Weight", field: "weight" },
      { title: "Doctor ID", field: "doctorid" },
    ],
    // data: data_fn(name_list, id_list),
    data: [],
    push: { name: null, id: null },
  });

  const get_data = (push) => {
    let s = [];
    //console.log("push =",push)
    for (let j = 0; j < name_list.length + 1; j++) s[j] = create_object();
    for (let j = 0; j < name_list.length + 1; j++) {
      s[j].name = push[j].name;
      s[j].id = push[j].id;
    }
    //console.log(q)
    return s;
  };
  const get_data2 = (push) => {
    let s = [];
    //console.log("push =",push)
    for (let j = 0; j < name_list.length; j++) s[j] = create_object();
    for (let j = 0; j < name_list.length; j++) {
      s[j].name = push[j].name;
      s[j].id = push[j].id;
    }
    //console.log(q)
    return s;
  };
  const get_data3 = (push) => {
    let s = [];
    //console.log("push =",push)
    for (let j = 0; j < name_list.length - 1; j++) s[j] = create_object();
    for (let j = 0; j < name_list.length - 1; j++) {
      s[j].name = push[j].name;
      s[j].id = push[j].id;
    }
    //console.log(q)
    return s;
  };
  // let [state, setState] = React.useState({
  //   columns: [
  //     { title: 'Name', field: 'name' },
  //     { title: 'Unique ID', field: 'id' },
  //   ],
  //   data: [
  //     { name: 'Mehmet', id: 307 },
  //     { name: 'Zerya ',id:891},
  //     { name: 'Kumar',id:644},
  //     { name: 'Kovind19',id:6969}
  //   ],
  // });
  //getApiData();
  // read_details();
  return (
    <div>
      <MaterialTable
        title="Registered Patients"
        columns={state.columns}
        data={state.data}
        options={{
          paging: false,
        }}
        editable={{
          onRowAdd: (newData) =>
            new Promise((resolve) => {
              setTimeout(() => {
                resolve();
                setState((prevState) => {
                  const data = [...prevState.data];
                  data.push(newData);
                  state.push = data;
                  let s = get_data(state.push); //object to be pushed with api
                  console.log(JSON.stringify(s));
                  // do api request to send state.push below
                  // axios.post('',JSON.stringify(s))
                  // .then((response)=> console.log(response))
                  // .catch((err)=>console.log(err))
                  return { ...prevState, data };
                });
              }, 600);
            }),
          onRowUpdate: (newData, oldData) =>
            new Promise((resolve) => {
              setTimeout(() => {
                resolve();
                if (oldData) {
                  console.log("oldData: ", oldData);
                  setState((prevState) => {
                    const data = [...prevState.data];
                    state.push = state.data;
                    // console.log(state.push)
                    // console.log("new data: ",newData)
                    state.push[data.length - 1] = newData;
                    let s = get_data2(state.push); //object to be pushed
                    //do api request to send state.push below
                    data[data.indexOf(oldData)] = newData;
                    return { ...prevState, data };
                  });
                }
              }, 600);
            }),
          onRowDelete: (oldData) =>
            new Promise((resolve) => {
              setTimeout(() => {
                resolve();
                setState((prevState) => {
                  const data = [...prevState.data];
                  data.splice(data.indexOf(oldData), 1);
                  console.log("data ", data);
                  state.push = data;
                  let s = get_data3(state.push); //object to pushed
                  //do api request
                  // console.log("s= ",s)
                  return { ...prevState, data };
                });
              }, 600);
            }),
        }}
      />
    </div>
  );
}
