import React from 'react';
import './sidebar.scss';
import './App.css';
import { BrowserRouter,Switch, Route, Redirect, Link} from 'react-router-dom';
import {DetailsLinker,VitalsLinker} from './components/link'
import {Details,Vitals} from './components/patient_componenets'

export class PatientLayout extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      leftOpen: false,
      details:false,
      vitals:false
    }
    this.checker_details=this.checker_details.bind(this)
    this.checker_vitals=this.checker_vitals.bind(this)
    this.update_state=this.update_state.bind(this)
    this.state_log=this.state_log.bind(this)
  }

  toggleSidebar = (event) => {
    let key = `${event.currentTarget.parentNode.id}Open`;
    this.setState({ [key]: !this.state[key] });
  }

  update_state(){
    this.setState(
      {details:false,
       vitals:false
      })

  }

  checker_details(){
    // this.setState({
    //   details:true
    // })  
    let x=1
    return x 
}
  checker_vitals(){
    // this.setState({
    //   vitals:true
    // })
    let x=2
    return x 
}
  state_log(){
    console.log("Details",this.state.details)
    console.log("Vitals",this.state.vitals)
  }

  render() {
    let leftOpen = this.state.leftOpen ? 'open' : 'closed';
    let link_btn
    if(this.state.details){
      link_btn=<Details />
      // this.update_details()
    }
    if(this.checker_details===1)
      link_btn=1
    if(this.checker_vitals===2)
      link_btn=2
    return (
      <div id='layout'>
          <div id='left' className={leftOpen} >
              <div  className='icon'  onClick={this.toggleSidebar}  >
                  &equiv;
              </div>
              <div className={`sidebar ${leftOpen}`} >
              {/* style={{borderBottom:"5px solid #F0F8FF"}} >  style for classname='header */}
                  <div className='header'>
                    <h3 className='tit' >
                      Covid-19 Patient Monitor
                    </h3>
                  </div>
                  <div className='content'>
                      <h3>
                        Welcome Patient_Name <br></br>
                        Patient ID: 
                      </h3>
                      <button onClick={ () => {this.checker_details();this.state_log()}}>Details</button>
                      <br></br>
                      <button onClick={ () => {this.checker_vitals();this.state_log()}}>Vitals</button>
                      <p>
                        <button id="logout_btn">LOG OUT</button>
                      </p>
                  </div>
              </div>
          </div>

          <div id='main'>
              <div className='header'>
                  <h1 className={`
                      tit
                      ${'left-' + leftOpen}
                  `}>
                      Patient Portal
                  </h1>
              </div>
              <div className='content'>
                  {/* <DetailsLinker /> */}
                {link_btn===1?<Details />:<Vitals/>}
                {/* {this.update_state} */}
              </div>
          </div>


      </div>
    );
  }
}
