//vitals import
import List from "../vitals.json"
import Details from "../vitals2.json"
//api import
// import { AxiosProvider, Request, Get, Delete, Head, Post, Put, Patch, withAxios } from 'react-axios'
import axios from 'axios'
//Imports for vitals table
import React,{Component} from 'react';
import { ThemeProvider,createMuiTheme,withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';





//table fns and componenets:
const StyledTableCell = withStyles(theme => ({
    
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white
    },
    body: {
      fontSize: 14
    }
  }))(TableCell);
  
  const StyledTableRow = withStyles(theme => ({
    root: {
      "&:nth-of-type(odd)": {
        backgroundColor: theme.palette.action.hover
      }
    }
  }))(TableRow);
  
  // function createData(patientId, bloodPressuree, respiratoryRate) {
  //   return { patientId, bloodPressuree, respiratoryRate};
  // }

  function createData(patientId,bloodPressuree, respiratoryRate,heartRate,spO2,temperature, isCritical,time, message) {
    return { patientId,bloodPressuree, respiratoryRate,heartRate,spO2,temperature, isCritical,time, message}
  }
  
// const rows = [
//     createData("Blood Pressure", vitals.bp, "130-140"),
//     createData("Temparature", vitals.temp, "130-140"),
//     createData("Cholestrol", vitals.c, "130-140"),
//     createData("Blood-Sugar", vitals.bs, "130-140")
//   ]  


  const useStyles = makeStyles({
    table: {
      minWidth: 200
    } 
  });
  


  export  function VitalsTable2(props) {
    const classes = useStyles();
    //read vitals data from json:
let vitals={
  bloodPressure:null,
  respiratoryRate:null,
  heartRate:null,
  patientId:null,
  spO2:null,
  temperature:null,
  isCritical:null,
  time:null,
  message:null
}
function read_data(vitals){
  List.forEach((p) => {
      //check the name here if(p.id===params.id)
      if(p.p_id===props.id)
        vitals.bloodPressure=p.bp
        vitals.temperature=p.temp
        vitals.spO2=p.c
        vitals.time=p.bs
  })
  // componentDidMount();
  console.log("vitals =",vitals)
  return vitals
}
function read_details(){
  console.log("reached fn")
  let item=[];
  for(let i=1;i<Object.keys(Details).length;i++){
    item.push(Details[i]);
  }
  //console.log("items ",item)
 return item;
}

vitals=read_data(vitals);
    const rows = [
      createData("Blood Pressure", vitals.bloodPressure, "130-140"),
      createData("Temparature", vitals.temperature, "130-140"),
      createData("Cholestrol", vitals.spO2, "130-140"),
      createData("Blood-Sugar", vitals.time, "130-140")
    ]

let items=read_details()
const rows1=[];
for(let i=0;i<items.length;i++){
  rows1.push(createData(items[i].bloodPressure,items[i].respiratoryRate,items[i].heartRate,items[i].patientId,items[i].spO2,items[i].temperature,items[i].isCritical,items[i].time,items[i].message))
}
console.log("rows= ",rows1);


    const theme= createMuiTheme({
      overrides:{
        MuiTableContainer:{
          root:{
            width:"800px",
            marginTop:"150px",
            marginLeft: "380px",
        }
      }
    }
})

    return (
      <div>
        {/* {window.location.href=="http://localhost:3000/vitals"&& */}
        <ThemeProvider theme={theme}>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell align="right">Patients ID</StyledTableCell>
              <StyledTableCell>Blood Pressure</StyledTableCell>
              <StyledTableCell align="right">Respiratory Rate</StyledTableCell>
              <StyledTableCell align="right">Heart Rate</StyledTableCell> 
              <StyledTableCell align="right">SpO2 Level</StyledTableCell>
              <StyledTableCell align="right">Temparature</StyledTableCell>
              <StyledTableCell align="right">isCritical</StyledTableCell>
              <StyledTableCell align="right">Log Time</StyledTableCell>
              <StyledTableCell align="right">Note</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows1.map(row => (
              <StyledTableRow key={row.name}>
                <StyledTableCell component="th" scope="row">{row.name}</StyledTableCell>
                <StyledTableCell align="right">{row.patientId}</StyledTableCell>
                <StyledTableCell align="right">{row.bloodPressure}</StyledTableCell>
                <StyledTableCell align="right">{row.respiratoryRate}</StyledTableCell>
                <StyledTableCell align="right">{row.heartRate}</StyledTableCell> 
                <StyledTableCell align="right">{row.spO2}</StyledTableCell>
                <StyledTableCell align="right">{row.temperature}</StyledTableCell>
                <StyledTableCell align="right">{row.isCritical}</StyledTableCell>
                <StyledTableCell align="right">{row.time}</StyledTableCell>
                <StyledTableCell align="right">{row.message}</StyledTableCell> 
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>//}
      </ThemeProvider>
      </div>
      
    );
  }
