import React from 'react';
import MaterialTable from 'material-table';
import {PatTableExtract} from './listextractor'

export  function PatTable() {
  let [name_list,id_list]=PatTableExtract();
  let create_object=()=>{
    return {name:null,id:null}
  }
//try to make a func which would return {name:null,id:null} and make this return happen as many times as there are names in the name list
  let data_fn = (name_list,id_list) => {
    let data=[];
    for(let j=0;j<name_list.length;j++)
      data[j]=create_object()
    // data[0].name=n.forEach((p)=>p)
    for(let j=0;j<name_list.length;j++){
      data[j].name=name_list[j]
      data[j].id=id_list[j]
    }
    return data
  }
  let [state, setState] = React.useState({
    columns: [
      { title: 'Name', field: 'name' },
      { title: 'Unique ID', field: 'id' },
    ],
    data: data_fn(name_list,id_list),
    push:{name:null,id:null}
  });

  const get_data = (push) => {
    let list=[]
    for(let j=0;j<name_list.length;j++)
      list[j]=create_object()
    for(let j=0;j<name_list;j++){
      list[j].name=push[j].name
      list[j].id=push[j].id
    }
    console.log(list)
    return list
  }

  // let [state, setState] = React.useState({
  //   columns: [
  //     { title: 'Name', field: 'name' },
  //     { title: 'Unique ID', field: 'id' },
  //   ],
  //   data: [
  //     { name: 'Mehmet', id: 307 },
  //     { name: 'Zerya ',id:891},
  //     { name: 'Kumar',id:644},
  //     { name: 'Kovind19',id:6969}
  //   ],
  // });
  


  return (
    <div>
    <MaterialTable
      title="Registered Doctors"
      columns={state.columns}
      data={state.data}
      options={{
          paging:false
      }}
      editable={{
        onRowAdd: (newData) =>
        new Promise((resolve) => {
          setTimeout(() => {
            resolve();
            setState((prevState) => {
              const data = [...prevState.data];
              data.push(newData);
              state.push=data;
              let list=get_data(state.push)
              console.log(list)
              //do api request to send state.push
              // console.log(state.push)
              return { ...prevState, data };
            });
          }, 600);

        }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                  console.log("oldData: ",oldData)
                setState((prevState) => {
                  const data = [...prevState.data];
                  state.push=newData
                  console.log("new data: ",state.data)
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
      }}
    />
    </div>
  );
}
