import React, { useState, useEffect } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Select from "react-select";
import { Link, Redirect } from "react-router-dom";
import axios from "axios";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      Covid-19 Patient Monitor {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export function SignIn() {
  const classes = useStyles();
  const options = [
    { value: "patient", label: "Patient" },
    { value: "doc", label: "Doctor" },
    { value: "admin", label: "Administrator" },
  ];
  const [state, setState] = React.useState({
    selected: "",
  });
  //define state to hold input field values
  const [email, setEmail] = useState("");
  const [id, setId] = useState("");
  const [type, setType] = useState("");
  const [password, setPassword] = useState("");

  // const decide_link = () => {
  //   if (state.selected === "doc") return "/doctor";
  //   else if (state.selected === "patient") return "/patient";
  //   else if (state.selected === "admin") return "/admin";
  //   else return "/signin";
  // };
  const decide_link = () => {
    if (type === "doctor") return "/doctor";
    else if (type === "patient") return "/patient";
    else if (type === "admin") return "/admin";
    else return "/signin";
  };
  const handle_submit = async () => {
    console.log("reached");
    await axios
      .post("http://0f6e5988c9eb.ngrok.io/covid19_patient_monitor/login", {
        email: email,
        password: password,
      })
      .then((res) => {
        if (res.data.error === "none") {
          console.log("response", res);
          setId(res.data.id);
          setType(res.data.type);
        } else {
          alert("ERROR ", res.data.error);
          <Link to="/signin" />;
        }
      })
      .catch((err) => console.log(err));
  };
  const handle_select = (e) => {
    console.log("reached");
    console.log(e.value);
    setState({
      ...state,
      selected: e.value,
    });
  };
  // useEffect(() => {
  //   handle_submit();
  // }, []);
  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="userid"
            label="Enter Email"
            name="email"
            //autoComplete="email"
            autoFocus
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            onChange={(e) => setPassword(e.target.value)}
          />
          {/* <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          /> */}
          <Grid item xs={12}>
            Choose User Type:
            <Select options={options} onChange={handle_select} />
          </Grid>
          <Link to={decide_link}>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick={handle_submit}
            >
              Sign In
            </Button>
          </Link>
          <Grid container>
            <Grid item>
              <a href="/signup">{"Don't have an account? Sign Up"}</a>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
        <Copyright />
      </Box>
    </Container>
  );
}
