//vitals import
import List from "../vitals.json"
//Imports for vitals table
import React,{Component} from 'react';
import { ThemeProvider,createMuiTheme,withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';


//read vitals data from json:
let vitals={
    bp:null,
    temp:null,
    c:null,
    bs:null
}
List.forEach((p) => {
    //check the name here if(p.id===paramas.id)
    vitals.bp=p.bp
    vitals.temp=p.temp
    vitals.c=p.c
    vitals.bs=p.bs
})


//table fns and componenets:
const StyledTableCell = withStyles(theme => ({
    
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white
    },
    body: {
      fontSize: 14
    }
  }))(TableCell);
  
  const StyledTableRow = withStyles(theme => ({
    root: {
      "&:nth-of-type(odd)": {
        backgroundColor: theme.palette.action.hover
      }
    }
  }))(TableRow);
  
  function createData(name, calories, fat, carbs) {
    return { name, calories, fat, carbs};
  }
  
  const rows = [
    createData("Blood Pressure", 159, "130-140"),
    createData("Temparature", 237, "130-140"),
    createData("Cholestrol", 262, "130-140"),
    createData("Blood-Sugar", 69, "130-140")
  ];
  
  const useStyles = makeStyles({
    table: {
      minWidth: 200
    } 
  });
  
  export  function VitalsTable() {
    const classes = useStyles();
    return (
  
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell>Parameter</StyledTableCell>
              <StyledTableCell align="right">Value</StyledTableCell>
              <StyledTableCell align="right">Safe Range</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map(row => (
              <StyledTableRow key={row.name}>
                <StyledTableCell component="th" scope="row">
                  {row.name}
                </StyledTableCell>
                <StyledTableCell align="right">{row.calories}</StyledTableCell>
                <StyledTableCell align="right">{row.fat}</StyledTableCell>
                {/* <StyledTableCell align="right">{row.carbs}</StyledTableCell> */}
                {/* <StyledTableCell align="right">{row.protein}</StyledTableCell> */}
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      
    );
  }
