import axios from "axios";
import React, { Component, useState } from "react";
import MaterialTable from "material-table";
import { Editable } from "./tablefunction";
export class PatTableTemp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      res: null,
      columns: [
        { title: "Name", field: "name" },
        { title: "Patient ID", field: "patientId" },
        { title: "Email", field: "email" },
        { title: "Address", field: "address" },
        { title: "Phone Number", field: "phone" },
        { title: "Gender", field: "sex" },
        { title: "Name of Guardian", field: "guardianName" },
        { title: "Age", field: "age" },
        { title: "Height", field: "height" },
        { title: "Weight", field: "weight" },
        { title: "Doctor ID", field: "doctorId" },
      ],
      data: [
        {
          name: "sumo",
          patientId: "12",
          email: "@.com",
          address: "teynampet",
          phone: "12",
          sex: "male",
          guardianName: "cba",
          age: "18",
          height: "12",
          weight: "45",
          doctorId: "619",
        },
      ],
    };
    this.add_data = this.add_data.bind(this);
  }

  getApiData() {
    let create_object = () => {
      return {
        name: 0,
        patientId: 0,
        email: 0,
        password: 0,
        address: 0,
        phone: 0,
        sex: 0,
        guardianName: 0,
        age: 0,
        height: 0,
        weight: 0,
        doctorId: 0,
      };
    };
    axios
      .get(this.props.url + "covid19_patient_monitor/get_info", {
        params: {
          a_id: "ADMIN123",
        },
      })
      .then((response) => {
        console.log("api response ", response.data.patients);
        this.setState({
          res: response.data.patients,
        });
        // response = res.data.patients;
        //setRes(res.data.patients);
      })
      .catch((err) => console.log("error ", err));
  }
