import React from 'react';
import './sidebar.scss';
import './App.css';
import {Alerts,Patients} from './components/doctor_components';

export class DoctorLayout extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      leftOpen: false
    }
    //this.decide_color=this.decide_color.bind(this)
    //this.toggleSidebar=this.toggleSidebar.bind(this)
  }

  toggleSidebar = (event) => {
    let key = `${event.currentTarget.parentNode.id}Open`;
    this.setState({ [key]: !this.state[key] });
    
  }
  decide_color(){
/*     if(!this.state.leftOpen)
      console.log("true") */
      console.log("reached the fn!")
  }

  render() {
    let leftOpen = this.state.leftOpen ? 'open' : 'closed';
  
    return (
      <div id='layout'>
          <div id='left' className={leftOpen} >
              <div style={{borderBottom:"5px solid #F0F8FF"}} className='icon' //this is 3 bar button
                    onClick={() => {this.decide_color()} } 
                    >
{/* /*                     onClick={() => {
                      this.toggleSidebar.bind();
                      //this.decide_color();
                    }}> */}
                   &equiv;
              </div>
              <div className={`sidebar ${leftOpen}`} >
                  <div className='header' style={{borderBottom:"5px solid #F0F8FF"}}>
                    <h3 className='tit'>
                      Covid-19 Patient Monitor
                    </h3>
                  </div>
                  <div className='content'>
                      <h3>
                        Welcome Doctor_Name <br></br>
                        Doc ID: 
                      </h3>
                      <p>
                        <button id="logout_btn">LOG OUT</button>
                      </p>
                  </div>
              </div>
          </div>

          <div id='main'>
              <div className='header'>
                  <h1 className={`
                      tit
                      ${'left-' + leftOpen}
                  `}>
                      Doctor Portal
                  </h1>
              </div>
              <div className='content'>
                  <br/>
                  <div className="alert_box">
                    <Alerts />
                  </div>
                  <div className="patient_box">
                    <Patients />
                  </div>
              </div>
          </div>


      </div>
    );
  }
}
