import React, { Component, useState } from "react";
import axios from "react";
import { DocTable } from "./docstable";
import { PatTable } from "./pattable";
import { StylesProvider } from "@material-ui/core/styles";
//import for register form
import "./form.css";

export class Patients extends Component {
  render() {
    return (
      <div>
        <div className="docpat">
          <StylesProvider injectFirst>
            <PatTable />
          </StylesProvider>
        </div>
      </div>
    );
  }
}

export class Doctors extends Component {
  render() {
    return (
      <div>
        <div className="docpat">
          <StylesProvider injectFirst>
            <DocTable />
          </StylesProvider>
        </div>
      </div>
    );
  }
}

export function RegisterPatient() {
  //define state to hold input field values
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [sex, setSex] = useState("");
  const [guardianName, setguardianName] = useState("");
  const [age, setAge] = useState("");
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [adminId, setdId] = useState("456");

  // function to spost data
  const sendData = async (post) => {
    try {
      axios.post(
        "http://81d40835b68c.ngrok.io/covid19_patient_monitor/doctor",
        post
      );
    } catch (err) {
      console.log("error ", err);
    }
  };
  const handleSubmit = () => {
    let post = {
      name: name,
      address: address,
      email: email,
      phone: phone,
      sex: sex,
      guardianName: guardianName,
      age: age,
      height: height,
      weight: weight,
      adminId: adminId,
    };
    post = JSON.stringify(post);

    //send post data to API
    sendData(post);
  };

  return (
    <div>
      <div class="wrapper">
        <div class="title">Patient Registration</div>
        <div class="form">
          <div class="inputfield">
            <label>Name</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Email Address</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Phone Number</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Age</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setAge(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Height(cm)</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setHeight(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Weight(kg)</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setWeight(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Gender</label>
            <div class="custom_select">
              <select onChange={(e) => setSex(e.target.value)}>
                <option value="">Select</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
              </select>
            </div>
          </div>
          <div class="inputfield">
            <label>Guardian</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setguardianName(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Address</label>
            <textarea
              class="textarea"
              onChange={(e) => setAddress(e.target.value)}
            ></textarea>
          </div>
          <div class="inputfield">
            <input
              id="submit-btn"
              type="submit"
              value="Register"
              onClick={handleSubmit()}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export function RegisterDoctor() {
  //define state to hold input field values
  const [name, setName] = useState("");
  const [hospitalInfo, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [hospitalName, sethospitalName] = useState("");
  const [adminId, setdId] = useState("ADMIN123");

  // function to spost data
  const sendData = async (post) => {
    try {
      axios.post(
        "http://81d40835b68c.ngrok.io/covid19_patient_monitor/doctor",
        post
      );
    } catch (err) {
      console.log("error ", err);
    }
  };
  const handleSubmit = () => {
    let post = {
      name: name,
      address: address,
      email: email,
      phone: phone,
      sex: sex,
      guardianName: guardianName,
      age: age,
      height: height,
      weight: weight,
      adminId: adminId,
    };
    post = JSON.stringify(post);

    //send post data to API
    sendData(post);
  };

  return (
    <div>
      <div class="wrapper">
        <div class="title">Doctor Registration</div>
        <div class="form">
          <div class="inputfield">
            <label>Name</label>
            <input type="text" class="input" />
          </div>
          <div class="inputfield">
            <label>Email Address</label>
            <input type="text" class="input" />
          </div>
          <div class="inputfield">
            <label>Phone Number</label>
            <input type="number" class="input" />
          </div>
          <div class="inputfield">
            <label>Age</label>
            <input type="number" class="input" />
          </div>
          <div class="inputfield">
            <label>Hospital/Clinic Name</label>
            <input type="text" class="input" />
          </div>
          <div class="inputfield">
            <label>Hospital/Clinic Address</label>
