import React, {Component,useState,useEffect} from 'react'
import '../App.css'
import uparrow from '../up arrow.jpg'
import downarrow from '../down arrow.jpg'
import {Extract} from './listextractor'
import {Personlistmaker} from './personlist.js'
import {VitalsTable} from './vitalstable'
import {Links,Route} from 'react-router-dom'
import List from '../docpat.json'
import axios from 'axios'
//import for register form
import './form.css'

export class Alerts extends Component{
    constructor(props){
        super(props)
        this.state={
            name_list:[],
            id_list:[]
        }
        this.show_list=this.show_list.bind(this);
        //this.close_list=this.show_list.bind(this); binding here uses prev state, therefore bound in onClick
    }
    show_list(){
        this.setState({
            name_list:['Sumanth','Saketh','Kunar','Sunni']
        })
        //console.log(this.state.name_list)
    }
    close_list(){
        this.setState({
            name_list:[]
        })
        console.log("on change works!")
    }
    
    render(){
        return(
        <div>
            <div className="alert_box_title">
                Alerts
            </div>
            <div className="patients">
                Sumanth
            </div>
            <div className="patients">
                Rakesh
            </div>
            <div className="patients">
                Murkhi
            </div>
            <div className="patients">
                Kishore
            </div>
            {/* <div className="patients">
               <Personlistmaker name_list={this.state.name_list} />
           </div> */}
        </div>
        )
    }
}

export class Patients extends Component{
    constructor(props){
        super(props)
        this.state={
            name_list:[],
            id_list:[],
            patients_obj:null
        }
        this.show_list=this.show_list.bind(this);
        this.update_state=this.update_state.bind(this);
        //this.close_list=this.show_list.bind(this); binding here uses prev state, therefore bound in onClick
    }
    
    // extract(name){
    //     console.log("inisde list ectractor method")
    //     let name_list=[],id_list=[],i=0
    //     List.forEach((p)=>{
    //         if(p.name===name){
    //             name_list=p.patients.map((e)=>e[0])
    //             id_list=p.patients.map((e)=>e[1])
    //         }
    //     })
    //     console.log(id_list)
    //     return name_list
    // }


    show_list(temp_list,tempid_list){
        this.setState({
            name_list:temp_list,
            id_list:tempid_list
        })
        // this.setState({
        //     name_list:['a','c']
        // })
    }



    update_state(){

            //let [temp_list,tempid_list]=Extract("Kumar")
            //alert("reached the fn!")
            //console.log(temp_list);
        axios.get('http://81d40835b68c.ngrok.io/covid19_patient_monitor/doctor',{
        params:{
            d_id:456
        }
        })
        .then(function (response) {
            // handle success
            // console.log(response.data.patients);
            this.setState({patients_obj:response.data.patients})
            console.log(this.state.patients_obj)
        })
        .catch(function (error) {
            // handle error
            console.log(error);
        })
        let [temp_list,tempid_list]=Extract("Kumar")
        this.setState({
            name_list:temp_list,
            id_list:tempid_list
        })
            //alert(this.state.name_list)
        // }

    }

    close_list(){
        this.setState({
            name_list:[],
            id_list:[]
        })
        console.log("on change works!")
    }
    render(){
        return(
        <div>
        
            <div className="patient_box_title">
               Registered Patients
               <img src={downarrow} className="down" onClick={this.update_state.bind(this)}  height="20px" width="20px" role="button" tabIndex="0" alt="exapand"></img>
               <img src={uparrow}  className ="up"onClick={this.close_list.bind(this)} height="20px" width="20px" role="button" tabIndex="0" alt="exapand"></img>
           </div>
            <div className="patients">
                <Personlistmaker  name_list={this.state.name_list} id_list={this.state.id_list} /> 
           </div> 
        </div>
        )
    }
}


//registration ui
           
export  function Register() {
    
  
    return (
        <div>
            <div class="wrapper">
                <div class="title">
                Patient Registration
                </div>
                <div class="form">
                    <div class="inputfield">
                        <label>Name</label>
                        <input type="text" class="input" />
                    </div>  
                    <div class="inputfield">
                        <label>Email Address</label>
                        <input type="text" class="input"/>
                    </div> 
                    <div class="inputfield">
                        <label>Phone Number</label>
                        <input type="number" class="input"/>
                    </div> 
                    <div class="inputfield">
                        <label>Age</label>
                        <input type="number" class="input"/>
                    </div>
                    <div class="inputfield">
                        <label>Gender</label>
                        <div class="custom_select">
                            <select>
                            <option value="">Select</option>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                            </select>
                        </div>
                    </div>
                    <div class="inputfield">
                        <label>Guardian</label>
                        <input type="text" class="input"/>
                    </div>
                    <div class="inputfield">
                        <label>Address</label>
                        <textarea class="textarea"></textarea>
                    </div>
  

            </div>
            </div>  
        </div>
    );
  }
