import React, {Component} from 'react'
import '../App.css'
import uparrow from '../up arrow.jpg'
import downarrow from '../down arrow.jpg'
import {Personlistmaker} from './personlist.js'
import FlipMove from 'react-flip-move'

export class Basic extends Component{
    render(){
        return(
            <div>
                <h2>
                Welcome Doctor_Name<br></br>
                {/* <hr></hr> */}
                Doc ID:
                </h2>
            </div>
        )
    }
}

export class Alerts extends Component{
    render(){
        return(
        <div>
            <div className="title">
                Alerts
            </div>
            <div className="patients">
                Sumanth
            </div>
            <div className="patients">
                Saketh
            </div>
            <div className="patients">
                Kumar
            </div>
        </div>
        )
    }
}

export class Patients extends Component{
    constructor(props){
        super(props)
        this.state={
            name_list:[]
        }
        this.show_list=this.show_list.bind(this);
        //this.close_list=this.show_list.bind(this); binding here uses prev state, therefore bound in onClick
    }
    show_list(){
        this.setState({
            name_list:['Sumanth','Rakesh','Saketh','Rakim','Kunar']
        })
        //console.log(this.state.name_list)
    }
    close_list(){
        this.setState({
            name_list:[]
        })
        console.log("on change works!")
    }
    
    render(){
        return(
        <div>
            <div className="title">
               Registered Patients
               <img src={downarrow} id="down" onClick={this.show_list}  height="20px" width="20px" role="button" tabIndex="0" alt="exapand"></img>
               <img src={uparrow}  id ="up"onClick={this.close_list.bind(this)} height="20px" width="20px" role="button" tabIndex="0" alt="exapand"></img>
           </div>
           <div className="patients">
               <FlipMove duration={5000} easing="ease-in-out">
                    <Personlistmaker name_list={this.state.name_list} />
               </FlipMove>
           </div>
        </div>
        )
    }
}
