import axios from "axios";
import React, { Component, useState } from "react";
import MaterialTable from "material-table";
import { Editable } from "./tablefunction";
export class PatTableTemp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      table_data: [],
      columns: [
        { title: "Name", field: "name" },
        { title: "Patient ID", field: "patientId" },
        { title: "Email", field: "email" },
        { title: "Address", field: "address" },
        { title: "Phone Number", field: "phone" },
        { title: "Gender", field: "sex" },
        { title: "Name of Guardian", field: "guardianName" },
        { title: "Age", field: "age" },
        { title: "Height", field: "height" },
        { title: "Weight", field: "weight" },
        { title: "Doctor ID", field: "doctorId" },
      ],
      data: table_data,
      // {
      //   name: "sumo",
      //   patientId: "12",
      //   email: "@.com",
      //   address: "teynampet",
      //   phone: "12",
      //   sex: "male",
      //   guardianName: "cba",
      //   age: "18",
      //   height: "12",
      //   weight: "45",
      //   doctorId: "619",
      // },
    };
    this.add_data = this.add_data.bind(this);
    // this.formatApiData = this.formatApiData.bind(this);
  }

  create_object = () => {
    return {
      name: 0,
      patientId: 0,
      email: 0,
      password: 0,
      address: 0,
      phone: 0,
      sex: 0,
      guardianName: 0,
      age: 0,
      height: 0,
      weight: 0,
      doctorId: 0,
    };
  };
  getApiData() {
    axios
      .get(this.props.url + "covid19_patient_monitor/get_info", {
        params: {
          a_id: "ADMIN123",
        },
      })
      .then((response) => {
        console.log("api response ", response.data.patients);
        // this.setState({
        //   res: response.data.patients,
        // });
        let Details = response.data.patients;
        console.log("details ", Details);
        let d = [];
        for (let j = 0; j < Object.keys(Details).length; j++)
          d[j] = this.create_object();
        for (var key in Details) {
          d[key - 1].name = Details[key].name;
          d[key - 1].patientId = Details[key].patientId;
          d[key - 1].address = Details[key].address;
          d[key - 1].password = Details[key].password;
          d[key - 1].email = Details[key].email;
          d[key - 1].phone = Details[key].phone;
          d[key - 1].guardianName = Details[key].guardianName;
          d[key - 1].doctorId = Details[key].doctorId;
          d[key - 1].sex = Details[key].sex;
          d[key - 1].weight = Details[key].weight;
          d[key - 1].height = Details[key].height;
          d[key - 1].age = Details[key].age;
          console.log("d =", d);
          this.setState({
            table_data: d,
          });
        }
      })
      .catch((err) => console.log("error ", err));
  }

  //   formatApiData() {
  //     let Details = this.state.res;
  //     console.log("details ", Details);
  //     let d = [];
  //     for (let j = 0; j < Object.keys(Details).length; j++)
  //       d[j] = this.create_object();
  //     //console.log("table data: ", d);
  //     // console.log("d name ", d[0].name);
  //     for (var key in Details) {
  //       d[key - 1].name = Details[key].name;
  //       d[key - 1].patientId = Details[key].patientId;
  //       d[key - 1].address = Details[key].address;
  //       d[key - 1].password = Details[key].password;
  //       d[key - 1].email = Details[key].email;
  //       d[key - 1].phone = Details[key].phone;
  //       d[key - 1].guardianName = Details[key].guardianName;
  //       d[key - 1].doctorId = Details[key].doctorId;
  //       d[key - 1].sex = Details[key].sex;
  //       d[key - 1].weight = Details[key].weight;
  //       d[key - 1].height = Details[key].height;
  //       d[key - 1].age = Details[key].age;
  //     }
  //     // console.log("formatted ", d);
  //   }
  async componentDidMount() {
    this.getApiData();
  }

  add_data(data, newData) {
    console.log("state in add data", this.state);
    let arr = data;
    arr.push(newData);
    this.setState(
      {
        ...this.state,
        data: arr,
      },
      console.log("state in add data", this.state)
    );
  }

  render() {
    return (
      <>
        <MaterialTable
          title="Editable Preview"
          columns={this.state.columns}
          data={this.state.data}
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  this.add_data(this.state.data, newData);
                  //setData([...data, newData]);
                  resolve();
                }, 1000);
              }),
            // onRowUpdate: (newData, oldData) =>
            //   new Promise((resolve, reject) => {
            //     setTimeout(() => {
            //       const dataUpdate = [...data];
            //       const index = oldData.tableData.id;
            //       dataUpdate[index] = newData;
            //       setData([...dataUpdate]);

            //       resolve();
            //     }, 1000);
            //   }),
            // onRowDelete: (oldData) =>
            //   new Promise((resolve, reject) => {
            //     setTimeout(() => {
            //       const dataDelete = [...data];
            //       const index = oldData.tableData.id;
            //       dataDelete.splice(index, 1);
            //       setData([...dataDelete]);

            //       resolve();
            //     }, 1000);
            //   }),
          }}
        />
      </>
    );
  }
}
