import React from 'react';
import './sidebar.scss';
import './App.css';
import {Link} from './components/link';

export class PatientLayout extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      leftOpen: false
    }
    
  }

  toggleSidebar = (event) => {
    let key = `${event.currentTarget.parentNode.id}Open`;
    this.setState({ [key]: !this.state[key] });
  }

  decide_color(){
    let Style;
    if(this.state.leftOpen)
      Style={color:"#F0F8FF",borderBottom:"5px solid #F0F8FF"}
    else  
      Style={color:"#4682B4",borderBottom:"5px solid #4682B4"}
    return Style
  }

  render() {
    let leftOpen = this.state.leftOpen ? 'open' : 'closed';
    
    return (
      <div id='layout'>
          <div id='left' className={leftOpen} >
              <div  className='icon'  onClick={this.toggleSidebar}  >
                  &equiv;
              </div>
              <div className={`sidebar ${leftOpen}`} >
              {/* style={{borderBottom:"5px solid #F0F8FF"}} >  style for classname='header */}
                  <div className='header'>
                    <h3 className='tit' >
                      Covid-19 Patient Monitor
                    </h3>
                  </div>
                  <div className='content'>
                      <h3>
                        Welcome Patient_Name <br></br>
                        Patient ID: 
                      </h3>
                      <p>
                        <button id="logout_btn">LOG OUT</button>
                      </p>
                  </div>
              </div>
          </div>

          <div id='main'>
              <div className='header'>
                  <h1 className={`
                      tit
                      ${'left-' + leftOpen}
                  `}>
                      Patient Portal
                  </h1>
              </div>
              <div className='content'>
                  <Link />
              </div>
          </div>


      </div>
    );
  }
}
