import React from 'react';
import './sidebar.scss';
import './App.css';
import { BrowserRouter,Switch, Route, Redirect, Link} from 'react-router-dom';
import {DetailsLinker,VitalsLinker} from './components/link'
import {Details,Vitals} from './components/patient_componenets'

export class PatientLayout extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      leftOpen: false,
      details:false,
      vitals:false,
      link_btn:0
    }
    this.checker_details=this.checker_details.bind(this)
    this.checker_vitals=this.checker_vitals.bind(this)
    this.update_state=this.update_state.bind(this)
    this.create_component=this.create_component.bind(this)

  }

  toggleSidebar = (event) => {
    let key = `${event.currentTarget.parentNode.id}Open`;
    this.setState({ [key]: !this.state[key] });
  }

  update_state(){
    this.setState(
      {details:false,
       vitals:false
      })

  }

  checker_details(){
    this.setState({
      link_btn:1
    })  

}
  checker_vitals(){
    this.setState({
      link_btn:2
    })
}

  create_component(){
    let show
    if(this.state.link_btn===1)
       show=<Details />
    else if(this.state.link_btn===2)
       show=<Vitals />
    else 
       show= <div></div>
    return show
  }

  render() {
    
    let leftOpen = this.state.leftOpen ? 'open' : 'closed';
    let show=this.create_component()
    return (
      <div id='layout'>
          <div id='left' className={leftOpen} >
              <div  className='icon'  onClick={this.toggleSidebar}  >
                  &equiv;
              </div>
              <div className={`sidebar ${leftOpen}`} >
              {/* style={{borderBottom:"5px solid #F0F8FF"}} >  style for classname='header */}
                  <div className='header'>
                    <h3 className='tit' >
                      Covid-19 Patient Monitor
                    </h3>
                  </div>
                  <div className='content'>
                      <h3>
                        Welcome Patient_Name <br></br>
                        Patient ID: 
                      </h3>
                      <button onClick={ () => {this.checker_details();}}>Details</button>
                      <br></br>
                      <button onClick={ () => {this.checker_vitals();}}>Vitals</button>
                      <p>
                        <button id="logout_btn">LOG OUT</button>
                      </p>
                  </div>
              </div>
          </div>

          <div id='main'>
              <div className='header'>
                  <h1 className={`
                      tit
                      ${'left-' + leftOpen}
                  `}>
                      Patient Portal
                  </h1>
              </div>
              <div className='content'>
                <p>Welcome to the patient portal.
                   Here you can check your personal details and you vital health infromation
                   Access it via the side-bar  
                </p>
                {show}

              </div>
          </div>


      </div>
    );
  }
}
