import React, { Component, useState, useEffect } from "react";
import "../App.css";
import uparrow from "../up arrow.jpg";
import downarrow from "../down arrow.jpg";
import { Extract } from "./listextractor";
import { Personlistmaker } from "./personlist.js";
import { VitalsTable } from "./vitalstable";
import { Links, Route } from "react-router-dom";
import List from "../docpat.json";
import axios from "axios";
//import for register form
import "./form.css";

export class Alerts extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name_list: [],
      id_list: [],
    };
    this.show_list = this.show_list.bind(this);
    //this.close_list=this.show_list.bind(this); binding here uses prev state, therefore bound in onClick
  }
  show_list() {
    this.setState({
      name_list: ["Sumanth", "Saketh", "Kunar", "Sunni"],
    });
    //console.log(this.state.name_list)
  }
  close_list() {
    this.setState({
      name_list: [],
    });
    console.log("on change works!");
  }

  render() {
    return (
      <div>
        <div className="alert_box_title">Alerts</div>
        <div className="patients">Sumanth</div>
        <div className="patients">Rakesh</div>
        <div className="patients">Murkhi</div>
        <div className="patients">Kishore</div>
        {/* <div className="patients">
               <Personlistmaker name_list={this.state.name_list} />
           </div> */}
      </div>
    );
  }
}

export class Patients extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name_list: [],
      id_list: [],
      patients_obj: null,
    };
    this.show_list = this.show_list.bind(this);
    this.update_state = this.update_state.bind(this);
    // this.getstate = this.getstate.bind(this);
    //this.close_list=this.show_list.bind(this); binding here uses prev state, therefore bound in onClick
  }

  show_list(temp_list, tempid_list) {
    this.setState({
      name_list: temp_list,
      id_list: tempid_list,
    });
    // this.setState({
    //     name_list:['a','c']
    // })
  }

  getapiData = async () => {
    try {
      let res = await axios.get(
        "http://81d40835b68c.ngrok.io/covid19_patient_monitor/doctor",
        {
          params: {
            d_id: 456,
          },
        }
      );
      this.setState({ patients_obj: res.data.patients });
    } catch (err) {
      console.log("error ", err);
    }
  };
  update_state = async () => {
    //get api data
    await this.getapiData();
    let name_list = [],
      id_list = [];
    console.log("state outside", this.state.patients_obj);
    for (var key in this.state.patients_obj) {
      name_list.push(this.state.patients_obj[key]);
      id_list.push(key);
    }
    console.log(name_list, id_list);
    // let [temp_list, tempid_list] = Extract("Kumar");
    this.setState({
      name_list: name_list,
      id_list: id_list,
    });
    //alert(this.state.name_list)
    // }
  };

  close_list() {
    this.setState({
      name_list: [],
      id_list: [],
    });
    console.log("on change works!");
  }
  render() {
    return (
      <div>
        <div className="patient_box_title">
          Registered Patients
          <img
            src={downarrow}
            className="down"
            onClick={this.update_state.bind(this)}
            height="20px"
            width="20px"
            role="button"
            tabIndex="0"
            alt="exapand"
          ></img>
          <img
            src={uparrow}
            className="up"
            onClick={this.close_list.bind(this)}
            height="20px"
            width="20px"
            role="button"
            tabIndex="0"
            alt="exapand"
          ></img>
        </div>
        <div className="patients">
          <Personlistmaker
            name_list={this.state.name_list}
            id_list={this.state.id_list}
          />
        </div>
      </div>
    );
  }
}

//registration ui

export function Register() {
  //define state to hold input field values
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [sex, setSex] = useState("");
  const [guardianName, setguardianName] = useState("");
  const [age, setAge] = useState("");
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [doctorId, setdId] = useState("456");

  // function to spost data
  const sendData = async (post) => {
    try {
      axios.post(
        "http://dceed99d2fec.ngrok.io/covid19_patient_monitor/doctor",
        post
      );
    } catch (err) {
      console.log("error ", err);
    }
  };
  const handleSubmit = () => {
    let post = {
      name: name,
      address: address,
      email: email,
      phone: phone,
      sex: sex,
      guardianName: guardianName,
      age: age,
      height: height,
      weight: weight,
      doctorId: doctorId,
    };
    post = JSON.stringify(post);

    //send post data to API
    sendData(post);
  };

  return (
    <div>
      <div class="wrapper">
        <div class="title">Patient Registration</div>
        <div class="form">
          <div class="inputfield">
            <label>Name</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Email Address</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Phone Number</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Age</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setAge(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Height(cm)</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setHeight(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Weight(kg)</label>
            <input
              type="number"
              className="input"
              onChange={(e) => setWeight(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Gender</label>
            <div class="custom_select">
              <select onChange={(e) => setSex(e.target.value)}>
                <option value="">Select</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
              </select>
            </div>
          </div>
          <div class="inputfield">
            <label>Guardian</label>
            <input
              type="text"
              className="input"
              onChange={(e) => setguardianName(e.target.value)}
            />
          </div>
          <div class="inputfield">
            <label>Address</label>
            <textarea
              class="textarea"
              onChange={(e) => setAddress(e.target.value)}
            ></textarea>
          </div>
          <div class="inputfield">
            <input
              id="submit-btn"
              type="submit"
              value="Register"
              onClick={handleSubmit()}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
