import React from 'react';
import MaterialTable from 'material-table';
import {AdminExtract} from './listextractor'

export  function PatTable() {
  let name_list,id_list=AdminExtract();
  const [state, setState] = React.useState({
    
    columns: [
      { title: 'Name', field: 'name' },
      { title: 'Unique ID', field: 'id' },
    ],
    data: [
      { name: 'Mehmet', id: 307 },
      { name: 'Zerya ',id:891},
      { name: 'Kumar',id:644},
      { name: 'Kovind19',id:6969}
    ],
  });
  
  const log_state= ()=>{
    console.log(state)
  }

  return (
    <div>
    <button onClick={log_state}>LOg state</button>
    <MaterialTable
      title="Registered Doctors"
      columns={state.columns}
      data={state.data}
      options={{
          paging:false
      }}
      editable={{
        onRowAdd: (newData) =>
        new Promise((resolve) => {
          setTimeout(() => {
            resolve();
            setState((prevState) => {
              const data = [...prevState.data];
              data.push(newData);
              return { ...prevState, data };
            });
          }, 600);
        }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                  console.log("oldData: ",oldData)
                setState((prevState) => {
                  const data = [...prevState.data];
                  console.log("new data: ",newData)
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
      }}
    />
    </div>
  );
}
