import React from "react";
import "./sidebar.scss";
import "./App.css";
import { Alerts, Patients, Register } from "./components/doctor_components";

export class DoctorLayout extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      leftOpen: false,
      details: false,
      vitals: false,
      link_btn: 0,
      posts: [],
    };
    this.checker_reg = this.checker_reg.bind(this);
    this.checker_alerts = this.checker_alerts.bind(this);
    this.checker_patients = this.checker_patients.bind(this);
    this.update_state = this.update_state.bind(this);
    this.create_component = this.create_component.bind(this);
  }

  componentDidMount() {
    console.log("props= ", this.props);
  }

  toggleSidebar = (event) => {
    let key = `${event.currentTarget.parentNode.id}Open`;
    this.setState({ [key]: !this.state[key] });
  };
  update_state() {
    this.setState({ details: false, vitals: false });
  }

  checker_reg() {
    this.setState({
      link_btn: 1,
    });
  }
  checker_alerts() {
    this.setState({
      link_btn: 2,
    });
  }
  checker_patients() {
    this.setState({
      link_btn: 3,
    });
  }

  create_component() {
    let show;
    if (this.state.link_btn === 2)
      show = (
        <div>
          <Alerts />
        </div>
      );
    else if (this.state.link_btn === 3)
      show = (
        <div className="patient_box">
          <Patients />
        </div>
      );
    else if (this.state.link_btn === 1) {
      show = <Register />;
      //  this.componentDidMount()
    } else
      show = (
        <div className="patient_home">
          <h2>Welcome to the Doctor portal!</h2>
          <p>
            Here you can check your personal details and your vital health
            infromation<br></br>
            Access it via the side-bar
          </p>
        </div>
      );
    return show;
  }
  //Style={color:"#F0F8FF",borderBottom:"5px solid #F0F8FF",position:"relative",bottom:"10px"}
  decide_color() {
    let Style;
    if (this.state.leftOpen)
      Style = {
        color: "#F0F8FF",
        borderBottom: "5px solid #F0F8FF",
        position: "absolute",
        display: "block",
      };
    else
      Style = {
        color: "#4682B4",
        borderBottom: "5px solid #4682B4",
        position: "absolute",
        display: "block",
      };
    console.log(Style);
    return Style;
  }

  render() {
    let leftOpen = this.state.leftOpen ? "open" : "closed";
    let show = this.create_component();
    let Style = this.decide_color();
    return (
      <div id="layout">
        {console.log(this.props)}
        <div id="left" className={leftOpen}>
          <div className="icon" onClick={this.toggleSidebar} style={Style}>
            &equiv;
          </div>
          <div className={`sidebar ${leftOpen}`}>
            {/* style={{borderBottom:"5px solid #F0F8FF"}} >  style for classname='header */}
            <div
              className="header"
              style={{ borderBottom: "5px solid #F0F8FF" }}
            >
              <h3 className="tit">Covid-19 Patient Monitor</h3>
            </div>
            <div className="content">
              <h3>
                Welcome Doctor_Name <br></br>
                Doc ID:{this.props.match.id}
              </h3>
              <button
                onClick={() => {
                  this.checker_reg();
                }}
              >
                Registrations
              </button>
              <br></br>
              <button
                onClick={() => {
                  this.checker_alerts();
                }}
              >
                Alert
              </button>
              <br></br>
              <button
                onClick={() => {
                  this.checker_patients();
                }}
              >
                Patients
              </button>
              <br></br>
              <button
                onClick={() => {
                  this.setState({ link_btn: 0 });
                }}
              >
                Home
              </button>
              <p>
                <button id="logout_btn">LOG OUT</button>
              </p>
            </div>
          </div>
        </div>

        <div id="main">
          <div className="header" style={{ color: "#4682B4" }}>
            <h1
              className={`
                      tit
                      ${"left-" + leftOpen}
                  `}
            >
              Doctor Portal
            </h1>
          </div>
          <div className="content">{show}</div>
        </div>
      </div>
    );
  }
}
