//vitals import
import List from "../vitals.json"
// import Details from '../vitals2.josn'
//api import
// import { AxiosProvider, Request, Get, Delete, Head, Post, Put, Patch, withAxios } from 'react-axios'
import axios from 'axios'
//Imports for vitals table
import React,{Component} from 'react';
import { ThemeProvider,createMuiTheme,withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';





//table fns and componenets:
const StyledTableCell = withStyles(theme => ({
    
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white
    },
    body: {
      fontSize: 14
    }
  }))(TableCell);
  
  const StyledTableRow = withStyles(theme => ({
    root: {
      "&:nth-of-type(odd)": {
        backgroundColor: theme.palette.action.hover
      }
    }
  }))(TableRow);
  
  function createData(name, calories, fat, carbs) {
    return { name, calories, fat, carbs};
  }
  
// const rows = [
//     createData("Blood Pressure", vitals.bp, "130-140"),
//     createData("Temparature", vitals.temp, "130-140"),
//     createData("Cholestrol", vitals.c, "130-140"),
//     createData("Blood-Sugar", vitals.bs, "130-140")
//   ]  


  const useStyles = makeStyles({
    table: {
      minWidth: 200
    } 
  });
  
 

  export  function VitalsTable(props) {
    const classes = useStyles();
    //read vitals data from json:
let vitals={
  bp:null,
  temp:null,
  c:null,
  bs:null
}
function read_data(vitals){
  List.forEach((p) => {
      //check the name here if(p.id===params.id)
      if(p.p_id===props.id)
        vitals.bp=p.bp
        vitals.temp=p.temp
        vitals.c=p.c
        vitals.bs=p.bs
  })
let details={
  bp:null,
  rr:null,
  hr:null,
  pid:null,
  sp:null,
  temp:null,
  isCritical:null,
  time:null,
  msg:null
}
// function read_details()

//   Details.forEach((e)=>{
//     if(e===1)
//       details.bp=e.bloodPressure;
//   })

  console.log("details =",details)
  // return details
}

vitals=read_data(vitals);
    const rows = [
      createData("Blood Pressure", vitals.bp, "130-140"),
      createData("Temparature", vitals.temp, "130-140"),
      createData("Cholestrol", vitals.c, "130-140"),
      createData("Blood-Sugar", vitals.bs, "130-140")
    ]

    const theme= createMuiTheme({
      overrides:{
        MuiTableContainer:{
          root:{
            width:"800px",
            marginTop:"150px",
            marginLeft: "380px",
        }
      }
    }
})

    return (
      <div>
        {/* {window.location.href=="http://localhost:3000/vitals"&& */}
        {/* {read_details()} */}
      <ThemeProvider theme={theme}>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell>Parameter</StyledTableCell>
              <StyledTableCell align="right">Value</StyledTableCell>
              <StyledTableCell align="right">Safe Range</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map(row => (
              <StyledTableRow key={row.name}>
                <StyledTableCell component="th" scope="row">
                  {row.name}
                </StyledTableCell>
                <StyledTableCell align="right">{row.calories}</StyledTableCell>
                <StyledTableCell align="right">{row.fat}</StyledTableCell>
                {/* <StyledTableCell align="right">{row.carbs}</StyledTableCell> */}
                {/* <StyledTableCell align="right">{row.protein}</StyledTableCell> */}
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>//}
      </ThemeProvider>
      </div>
      
    );
  }
