import React from 'react';
import './sidebar.scss';
import './App.css';
import {Patients,Doctors, RegisterDoctor,RegisterPatient} from './components/admin_components'

export class AdminLayout extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      leftOpen: false,
      doctors:false,
      patients:false,
      link_btn:0
    }
    this.checker_details=this.checker_doctors.bind(this)
    this.checker_vitals=this.checker_patients.bind(this)
    this.update_state=this.update_state.bind(this)
    this.create_component=this.create_component.bind(this)
    this.checker_regdoctors=this.checker_regdoctors.bind(this)
    this.checker_regpatients=this.checker_regpatients.bind(this)

  }

  toggleSidebar = (event) => {
    let key = `${event.currentTarget.parentNode.id}Open`;
    this.setState({ [key]: !this.state[key] });
  }

  update_state(){
    this.setState(
      {details:false,
       vitals:false
      })

  }

  checker_doctors(){
    this.setState({
      link_btn:1
    })  

}
  checker_patients(){
    this.setState({
      link_btn:2
    })
}
checker_regpatients(){
  this.setState({
    link_btn:3
  })
}
checker_regdoctors(){
  this.setState({
    link_btn:4
  })  

}

  create_component(){
    let show
    if(this.state.link_btn===1)
       show=<Doctors />
    else if(this.state.link_btn===2)
       show=<Patients />
    else if(this.state.link_btn===3)
      show=<RegisterPatient />
    else if(this.state.link_btn===4)
      show=<RegisterDoctor />
    else 
       show=<div className="patient_home">
       <h2>Welcome to the Admin portal!</h2>
       <p>Here you can create,edit or delete any doctor or patient records<br></br>
       Access these functions from the side-bar  
       </p>
    </div>
    return show
  }

  decide_color(){
    let Style;
    if(this.state.leftOpen)
      Style={color:"#F0F8FF",borderBottom:"5px solid #F0F8FF",position:"absolute",display:"block"}
    else  
      Style={color:"#4682B4",borderBottom:"5px solid #4682B4",position:"absolute",display:"block"}
    console.log(Style)
    return Style
  }

  render() {
    
    let leftOpen = this.state.leftOpen ? 'open' : 'closed';
    let show=this.create_component()
    let Style=this.decide_color()
    return (
      <div id='layout'>
          <div id='left' className={leftOpen} >
              <div  className='icon'  onClick={this.toggleSidebar} style={Style} >
                  &equiv;
              </div>
              <div className={`sidebar ${leftOpen}`} >
              {/* style={{borderBottom:"5px solid #F0F8FF"}} >  style for classname='header */}
                  <div className='header' style={{borderBottom:"5px solid #F0F8FF"}}>
                    <h3 className='tit' >
                      Covid-19 Patient Monitor
                    </h3>
                  </div>
                  <div className='content'>
                      <h3>
                        Welcome Admin_Name <br></br>
                        Admin ID: 
                      </h3>
                      <button onClick={ () => {this.checker_doctors();}}>Registered Doctors</button>
                      <br></br>
                      <button onClick={ () => {this.checker_patients();}}>Registered Patients</button>
                      <br></br>
                      <button onClick={ () => {this.checker_regdoctors();}}>Patient Registration</button>
                      <br></br>
                      <button onClick={ () => {this.checker_regpatients();}}>Doctor Registration</button>
                      <br></br>
                      <button onClick={ () => {this.setState({link_btn:0})}}>Home</button>
                      <p>
                        <button id="logout_btn">LOG OUT</button>
                      </p>
                  </div>
              </div>
          </div>

          <div id='main'>
              <div className='header' style={{color:"#4682B4"}}>
                  <h1 className={`
                      tit
                      ${'left-' + leftOpen}
                  `}>
                      Admin Portal
                  </h1>
              </div>
              <div className='content'>

                {show}

              </div>
          </div>


      </div>
    );
  }
}
