import React, { Component } from 'react'
import { BrowserRouter,Switch, Route, Redirect, Link} from 'react-router-dom';
import {SignIn} from './signin'
import { SignUp } from './signup';
import {Home} from './home'

export class Run extends Component {

    constructor(props){
        super(props)
        this.State ={
        signinClicked:0,
        signupClicked:0
        }
        this.trigger_signin=this.trigger_signin.bind(this);
        this.trigger_signup=this.trigger_signup.bind(this);
        this.create_component=this.create_component.bind(this);
    }

    trigger_signin(){
        this.setState({
            signinClicked: 1
        })
        
    }

    trigger_signup(){
        this.setState({
            signupClicked: 1
        })
        
    }

    create_component () {
        let show
        if(this.state.signinClicked===1)
            show=<SignIn/>
        else if(this.state.signupClicked===1)
            show=<SignUp/>
        else
            show=
            <div> 
                 <Home/>
                <div className="signin">
                    Have an account? Log in 
                    <button className="in" onClick={ () => {this.trigger_signin();} } >SIGN IN</button>
                </div>
                <div className="signup">
                    Dont have an account? Sign up!
                    <button className="in" onClick={ () => {this.trigger_signup();} }>SIGN UP</button>
                </div>
            </div>;
        return show;
    }
    render(){
        let show=this.create_component()
        return (
            <div className="main" >
                {show}  
            </div>
        )
   
}
}
